import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE = 'http://localhost:5000/api';

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState({ items: [], total: 0, itemCount: 0 });
  const [activeView, setActiveView] = useState('products');
  const [checkoutInfo, setCheckoutInfo] = useState({ name: '', email: '' });
  const [receipt, setReceipt] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchProducts();
    fetchCart();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(`${API_BASE}/products`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const fetchCart = async () => {
    try {
      const response = await axios.get(`${API_BASE}/cart`);
      setCart(response.data);
    } catch (error) {
      console.error('Error fetching cart:', error);
    }
  };

  const addToCart = async (productId) => {
    try {
      await axios.post(`${API_BASE}/cart`, { productId, quantity: 1 });
      await fetchCart();
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Error adding item to cart');
    }
  };

  const removeFromCart = async (itemId) => {
    try {
      await axios.delete(`${API_BASE}/cart/${itemId}`);
      await fetchCart();
    } catch (error) {
      console.error('Error removing from cart:', error);
      alert('Error removing item from cart');
    }
  };

  const handleCheckout = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post(`${API_BASE}/checkout`, {
        customerInfo: checkoutInfo
      });
      setReceipt(response.data);
      setCart({ items: [], total: 0, itemCount: 0 });
      setCheckoutInfo({ name: '', email: '' });
    } catch (error) {
      console.error('Error during checkout:', error);
      alert('Error during checkout');
    } finally {
      setLoading(false);
    }
  };

  const closeReceipt = () => {
    setReceipt(null);
    setActiveView('products');
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Vibe Commerce</h1>
        <nav className="nav-tabs">
          <button 
            className={`tab ${activeView === 'products' ? 'active' : ''}`}
            onClick={() => setActiveView('products')}
          >
            Products
          </button>
          <button 
            className={`tab ${activeView === 'cart' ? 'active' : ''}`}
            onClick={() => setActiveView('cart')}
          >
            Cart ({cart.itemCount})
          </button>
        </nav>
      </header>

      <main className="main-content">
        {activeView === 'products' && (
          <div className="products-view">
            <h2>Our Products</h2>
            <div className="products-grid">
              {products.map(product => (
                <div key={product.id} className="product-card">
                  <div className="product-image">
                    <img src={product.image} alt={product.name} />
                  </div>
                  <div className="product-info">
                    <h3>{product.name}</h3>
                    <p className="product-description">{product.description}</p>
                    <div className="product-footer">
                      <span className="product-price">${product.price}</span>
                      <button 
                        className="add-to-cart-btn"
                        onClick={() => addToCart(product.id)}
                      >
                        Add to Cart
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeView === 'cart' && (
          <div className="cart-view">
            <h2>Shopping Cart</h2>
            {cart.items.length === 0 ? (
              <div className="empty-cart">
                <p>Your cart is empty</p>
                <button 
                  className="continue-shopping"
                  onClick={() => setActiveView('products')}
                >
                  Continue Shopping
                </button>
              </div>
            ) : (
              <>
                <div className="cart-items">
                  {cart.items.map(item => (
                    <div key={item.id} className="cart-item">
                      <img src={item.image} alt={item.name} className="cart-item-image" />
                      <div className="cart-item-details">
                        <h4>{item.name}</h4>
                        <p>${item.price} x {item.quantity}</p>
                      </div>
                      <div className="cart-item-total">
                        ${(item.price * item.quantity).toFixed(2)}
                      </div>
                      <button 
                        className="remove-btn"
                        onClick={() => removeFromCart(item.id)}
                      >
                        Remove
                      </button>
                    </div>
                  ))}
                </div>
                <div className="cart-summary">
                  <div className="total-section">
                    <h3>Total: ${cart.total.toFixed(2)}</h3>
                  </div>
                  <button 
                    className="checkout-btn"
                    onClick={() => setActiveView('checkout')}
                  >
                    Proceed to Checkout
                  </button>
                </div>
              </>
            )}
          </div>
        )}

        {activeView === 'checkout' && (
          <div className="checkout-view">
            <h2>Checkout</h2>
            <form onSubmit={handleCheckout} className="checkout-form">
              <div className="form-group">
                <label htmlFor="name">Full Name:</label>
                <input
                  type="text"
                  id="name"
                  value={checkoutInfo.name}
                  onChange={(e) => setCheckoutInfo({...checkoutInfo, name: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  value={checkoutInfo.email}
                  onChange={(e) => setCheckoutInfo({...checkoutInfo, email: e.target.value})}
                  required
                />
              </div>
              <div className="checkout-actions">
                <button 
                  type="button" 
                  className="back-btn"
                  onClick={() => setActiveView('cart')}
                >
                  Back to Cart
                </button>
                <button 
                  type="submit" 
                  className="place-order-btn"
                  disabled={loading}
                >
                  {loading ? 'Processing...' : 'Place Order'}
                </button>
              </div>
            </form>
          </div>
        )}
      </main>

      {receipt && (
        <div className="receipt-modal">
          <div className="receipt-content">
            <h2>Order Confirmed!</h2>
            <div className="receipt-details">
              <p><strong>Order ID:</strong> {receipt.orderId}</p>
              <p><strong>Customer:</strong> {receipt.customerInfo.name}</p>
              <p><strong>Email:</strong> {receipt.customerInfo.email}</p>
              <p><strong>Order Date:</strong> {new Date(receipt.timestamp).toLocaleString()}</p>

              <h4>Items:</h4>
              {receipt.items.map((item, index) => (
                <div key={index} className="receipt-item">
                  <span>{item.name}</span>
                  <span>{item.quantity} x ${item.price} = ${(item.quantity * item.price).toFixed(2)}</span>
                </div>
              ))}

              <div className="receipt-total">
                <strong>Total: ${receipt.total.toFixed(2)}</strong>
              </div>
            </div>
            <button className="close-receipt-btn" onClick={closeReceipt}>
              Continue Shopping
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
